package com.pangff.rm;import java.io.File;import java.text.Collator;import java.util.Comparator;import java.util.Locale;public class ComparatorByName implements Comparator<File>{          boolean isAscend;      public ComparatorByName(boolean isAscend){          this.isAscend = isAscend;      }      public int compare(File f1, File f2) {                    if(isAscend == true){              return Collator.getInstance(Locale.CHINESE).compare(f1.getName(), f2.getName());          } else {              return Collator.getInstance(Locale.CHINESE).compare(f2.getName(), f1.getName());           }                }    } 